	 -- WHERE rt.ResultTest = ie.ResultTest and ie.InstrumentCode ='DXI800' 
	 -- and rt.Accession = @accession and Status not in ('Final','cancelled') and ie.InstrumentCD<>''
	 -- and s.ResultTest is null

