   on a.Accession = an.Accession    
     LEFT OUTER JOIN dbo.tblArchiveReviewAccession ara (NOLOCK) ON a.Accession = ara.Accession  
   where (p.ptLastName LIKE ISNULL(@LastName, p.ptLastName)    
