	select acc.Accession, acc.ClientCode , acc.PatientID,  acc.PhysicianCode,  acc.ReceiveDate , acc.CollectionDate,  acc.OrderType , acc.ProblemSpec, acc.PrimaryInsuranceID , acc.PreAccession ,  
