           WHERE rt.Accession = @Accession  and  (rt.Status = 'Pending' or rt.Status = 'Hold')) THEN 'Pending'    
       ELSE 'Final'    
             END    
 ,a.AddScannedPaperWork  ,a.ExternalComments as ReportComment, a.Fasting, p.PTContactHomePhone   
    from tblAccessions a (nolock)     
