CREATE PROCEDURE [dbo].[spGetFinalReReleaseAccessions]  
	@AccessionTable MOTests READONLY
AS  
BEGIN  
	SET NOCOUNT ON;

	SELECT Accession, ExternalComments, ptLastName + ', ' + ptFirstName AS PatientName
	FROM dbo.tblAccessions a
