create PROCEDURE [dbo].[spGetPayers]
As
BEGIN

	
	SET NOCOUNT ON;


	SELECT Name + ' (' + InsuranceCode +')' AS Payer, InsuranceCode as Code
	FROM tblInsuranceCompanies 
	where Name is not null and Name <> ''
	UNION
	SELECT '' As Payer , '' As Code
	ORDER BY Payer asc
	
