IF @DateType like 'C' 
 
   IF @TestCodes like 'ALL'
        BEGIN	
			SELECT distinct ot.Accession , pat.ptLastName + ', ' + pat.ptFirstName As PatientName ,
			acc.ClientCode, cln.ClientName, ot.orderedTest as TestCode, otd.description,
