CREATE PROCEDURE [dbo].[spQCGetControl]        
         
 @OrderID as varchar(100),        
 @InstrumentCode as varchar (100)        
         
AS        
BEGIN        
        
 SELECT         
    OrderID        
    ,c.InstrumentCode        
    ,c.Description        
    ,TestCodes        
    ,Average        
    ,Deviation        
    ,ArchiveFlag        
    ,CreatedBy        
    ,CreateDateTime        
