 @BillType as varchar(100),   
 @Discount as varchar(100),
 @Group as bit,  
 @QSSLastUpdatedID as varchar(20)    
 --@QSSVersionNumber as int    
   
  
   
AS  
BEGIN  
DECLARE      
            @Order  varchar(100)  
          , @NextBillDate datetime  
          , @CPTCode varchar(100)  
          , @FeeDescription varchar(200)  
          , @TotalFeeAmt decimal(18,4)  
          , @ExpPaymentAmt decimal(18,4)  
