 @CurrentBillType varchar(20) = '' ,
 --@OrderType varchar(10) , 
 --@ProblemSpec bit , 
 --@BillToInsuranceID  varchar(20) = NULL ,
 --@InternalRequisitionNo  varchar(20) ,
 @Notes varchar(500)  = '' ,
 --@SpecimenDataXML xml,
 --@TestCodeDataXML xml,
