                                  @ptLastName  ,
                                  @ptFirstName  ,
                                  @ptMiddleName  ,
                                  @ptDateOfBirth   ,
                                  @ptSex   ,
                                  @ptSSN   ,
                                  @ptAddress1  ,
                                  @ptAddress2   ,
                                  @ptCity   ,
                                  @ptState  ,
                                  @ptZip   ,
                              @PTContactHomePhone   ,
                              @PTContactWorkPhone   ,
                              @PTContactCellPhone  ,
                              @PTContactEmail   ,
                              @PTContactFax   ,
                              @PrimaryInsuranceID   ,
                              @PrimaryPatRelationtoIns   ,
                              @PrimaryInsuredID   ,
                              @SecondaryInsuranceID   ,
                              @SecondaryPatRelationtoIns   ,
                              @SecondaryInsuredID,  
                              getDate() ,
                              getDate() ,
                              0,
                              @ClientCode,
                              @WorkInjuryDate, 
                              @AutoInjuryDate)
       end
