      PerformedBy = @PerformedBy,              
      MailoutFlag = @MailoutFlag,              
      MailoutLab = @MailOutLab,              
      RequiredSpeciments = @RequiredSpeciments,                    
      RequiredVolume = @RequiredVolume,              
      Preservative = @Preservative,              
