	@AutoReleaseFlag as bit,
	@Decimals as varchar(100),
	@Units as varchar(100),
	@Comment as varchar(max),
	@SuppressFlag as bit,
	@SecureFlag as bit,
	@LOINCcode as varchar(100),
	@InconsistentCommentCode as varchar(max), 
